import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the following users to be loaded
    await page.waitForSelector('.following-user', { timeout: 5000 });

    // Extract username and follower count from all .following-user elements
    const users = await page.$$eval('.following-user', (elements) => {
      return elements.map(el => {
        const username = el.querySelector('.username').innerText.trim();
        const followerCount = el.querySelector('.follower-count').innerText.trim();
        return { username, followerCount };
      });
    });

    // Take a screenshot after extraction
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_extraction.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_extraction.png'));

    return {
      success: true,
      action: "Extracted usernames and follower counts from following users",
      extractedData: users,
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
