import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Click on the upload button to open the upload modal
    await page.waitForSelector('#uploadBtn');
    await page.click('#uploadBtn');
    await page.waitForSelector('#videoFileInput');

    // Attempt to upload invalid files 5 times
    const invalidFiles = ['invalid_file.txt', 'invalid_file.docx', 'invalid_file.mp3', 'invalid_file.png', 'invalid_file.zip'];
    for (let i = 0; i < 5; i++) {
      await page.setInputFiles('#videoFileInput', path.join(__dirname, invalidFiles[i]));
      await page.waitForTimeout(1000); // Wait for a moment to observe the response
      await page.screenshot({ path: path.join(screenshotsDir, `step_2_invalid_upload_${i + 1}.png`) });
      screenshots.push(path.join(screenshotsDir, `step_2_invalid_upload_${i + 1}.png`));
    }

    // Return success message
    return {
      success: true,
      action: "Attempted to upload invalid files 5 times in the upload modal",
      extractedData: {},
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

