import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Click on the upload button to open the upload modal
    await page.waitForSelector('#uploadBtn');
    await page.click('#uploadBtn');
    await page.waitForSelector('#videoFileInput');

    // Define a list of invalid file formats to upload
    const invalidFiles = [
      'invalid_file.txt', // Text file
      'image.png',        // Image file
      'document.pdf',     // PDF file
      'audio.mp3',        // Audio file
      'archive.zip'       // Zip file
    ];

    // Attempt to upload invalid files 5 times
    for (let i = 0; i < 5; i++) {
      // Select the file input and upload an invalid file
      const invalidFilePath = path.join(__dirname, invalidFiles[i % invalidFiles.length]);
      await page.setInputFiles('#videoFileInput', invalidFilePath);

      // Wait for system response (error message or feedback)
      await page.waitForTimeout(1000); // Adjust this timeout if necessary

      // Take a screenshot after each upload attempt
      await page.screenshot({ path: path.join(screenshotsDir, `step_${i + 2}_upload_attempt.png`) });
      screenshots.push(path.join(screenshotsDir, `step_${i + 2}_upload_attempt.png`));
    }

    return {
      success: true,
      action: "Uploaded invalid files five times consecutively",
      extractedData: { attempts: 5 },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
