import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the first video to be visible
    await page.waitForSelector('.video-container', { timeout: 5000 });

    // Click the share button of the first video
    await page.click('button.action-btn.share-btn');

    // Wait for the share modal to appear
    await page.waitForSelector('.share-modal', { timeout: 5000 });

    // Click the 'Copy Link' option
    await page.click('text=Copy Link');

    // Take screenshot after copying the link
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_copy_link.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_copy_link.png'));

    return {
      success: true,
      action: "Clicked 'Copy Link' from the share modal of the first video",
      extractedData: {},
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

