import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the first video container to be present
    await page.waitForSelector('div.video-container[data-video-id="1"]');

    // Click the play/pause button within the first video container
    const playPauseBtn = await page.$('div.video-container[data-video-id="1"] .play-pause-btn');
    if (playPauseBtn) {
      await playPauseBtn.click();
      await page.waitForTimeout(500); // wait for half a second to ensure action is registered
    }

    // Take screenshot after clicking the button
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_after_click.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_after_click.png'));

    // Verify if the button state has changed (optional)
    const buttonState = await playPauseBtn.evaluate(btn => btn.innerHTML);

    return {
      success: true,
      action: "Clicked the play/pause button for the first video",
      extractedData: { buttonState },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
