import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Click the upload button to open the upload modal
    await page.waitForSelector('#uploadBtn');
    await page.click('#uploadBtn');
    await page.waitForLoadState('networkidle');

    // Wait for the caption input field to be visible
    await page.waitForSelector('#videoCaption');

    // Type 'Amazing video!' into the caption field
    await page.fill('#videoCaption', 'Amazing video!');

    // Take a screenshot after entering the caption
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_caption_filled.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_caption_filled.png'));

    return {
      success: true,
      action: "Entered 'Amazing video!' into the caption field",
      extractedData: {
        caption: await page.inputValue('#videoCaption')
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

