    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_search_result.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_search_result.png'));

    return {
      success: true,
      action: "Entered '#dance' in search input and clicked search button",
      extractedData: { searchQuery: '#dance' },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

