import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the comment input field to be visible
    await page.waitForSelector('#commentInput');

    // Type 'Great content!' into the comment input field
    await page.fill('#commentInput', 'Great content!');

    // Wait for the send button to be visible
    await page.waitForSelector('#commentSendBtn');

    // Click the send button
    await page.click('#commentSendBtn');

    // Take a screenshot after sending the comment
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_comment_sent.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_comment_sent.png'));

    // Verify if the comment was sent successfully (this can vary based on the implementation)
    const commentSent = await page.evaluate(() => {
      // Check if the comment appears in the comment section (this is a placeholder logic)
      return document.querySelector('.comment-section').innerText.includes('Great content!');
    });

    return {
      success: commentSent,
      action: "Comment 'Great content!' was sent successfully",
      extractedData: {},
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

