import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the comment input field to be visible and fill it with the comment
    await page.waitForSelector('#commentInput', { visible: true });
    await page.fill('#commentInput', 'Great content!');

    // Take screenshot after entering the comment
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_comment_entered.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_comment_entered.png'));

    // Wait for the send button and click it to submit the comment
    await page.waitForSelector('#commentSendBtn', { visible: true });
    await page.click('#commentSendBtn');

    // Take screenshot after sending the comment
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_comment_sent.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_comment_sent.png'));

    // Verify if the comment was successfully submitted (this may require checking the comment section)
    const commentExists = await page.evaluate(() => {
      return Array.from(document.querySelectorAll('.comment')).some(comment => comment.innerText.includes('Great content!'));
    });

    return {
      success: true,
      action: "Comment 'Great content!' was submitted successfully",
      extractedData: { commentExists },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
