import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the following users to load
    await page.waitForSelector('.following-user');

    // Extract username and follower count from all .following-user elements
    const users = await page.evaluate(() => {
      const userElements = document.querySelectorAll('.following-user');
      return Array.from(userElements).map(user => {
        const username = user.querySelector('.username-selector')?.textContent || '';
        const followers = user.querySelector('.follower-count-selector')?.textContent || '';
        return { username, followers };
      });
    });

    // Take a screenshot after extraction
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_extracted_data.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_extracted_data.png'));

    return {
      success: true,
      action: "Extracted usernames and follower counts from following user profiles",
      extractedData: users,
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
