import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Open upload modal
    await page.click('#uploadBtn');
    await page.waitForSelector('#videoFileInput');

    // Attempt to upload invalid files 5 times
    for (let i = 0; i < 5; i++) {
      // Select an invalid file (e.g., a text file)
      const invalidFilePath = path.resolve(__dirname, 'invalid_file.txt'); // Make sure this file exists
      await page.setInputFiles('#videoFileInput', invalidFilePath);

      // Take a screenshot after each upload attempt
      await page.screenshot({ path: path.join(screenshotsDir, `step_2_upload_attempt_${i + 1}.png`) });
      screenshots.push(path.join(screenshotsDir, `step_2_upload_attempt_${i + 1}.png`));

      // Wait for any error messages or system responses
      await page.waitForTimeout(1000); // Adjust timeout as necessary
    }

    return {
      success: true,
      action: "Uploaded invalid files 5 times in a row to test system response.",
      extractedData: {},
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

