import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Click the upload button to open the upload modal
    await page.waitForSelector('#uploadBtn');
    await page.click('#uploadBtn');
    await page.waitForSelector('#videoFileInput');

    // Define a list of invalid file formats to test
    const invalidFiles = [
      'invalid_file.txt', // Example of a text file
      'image.png',        // Example of an image file
      'document.pdf',     // Example of a PDF file
      'not_a_video.mp4',  // An MP4 file that is not valid for upload
      'script.js'         // Example of a JavaScript file
    ];

    for (const file of invalidFiles) {
      // Set the file input to the invalid file
      const filePath = path.join(__dirname, file); // Adjust path as necessary
      await page.setInputFiles('#videoFileInput', filePath);

      // Wait for the upload process to react (could be an error message or notification)
      await page.waitForTimeout(1000); // Wait for a second for the UI to respond

      // Take a screenshot after each upload attempt
      await page.screenshot({ path: path.join(screenshotsDir, `step_upload_attempt_${file}.png`) });
      screenshots.push(path.join(screenshotsDir, `step_upload_attempt_${file}.png`));
    }

    return {
      success: true,
      action: "Uploaded invalid file formats consecutively to test system response.",
      extractedData: {},
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
