import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the upload button to be visible and click it
    await page.waitForSelector('#uploadBtn', { state: 'visible' });
    await page.click('#uploadBtn');

    // Take screenshot after clicking the upload button
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_upload_modal_opened.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_upload_modal_opened.png'));

    // Verify if the upload modal is opened (you can add specific checks here if needed)
    const isModalVisible = await page.isVisible('.upload-modal'); // Assuming the modal has a class 'upload-modal'

    return {
      success: true,
      action: "Clicked the upload button to open the upload modal",
      extractedData: { isModalVisible },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

