import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the Users tab button to be visible and click it
    await page.waitForSelector(".following-tab-btn[data-tab='users']");
    await page.click(".following-tab-btn[data-tab='users']");

    // Take screenshot after clicking the Users tab
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_users_tab_clicked.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_users_tab_clicked.png'));

    // Verify that the Users tab is active
    const isUsersTabActive = await page.evaluate(() => {
      return document.querySelector(".following-tab-btn[data-tab='users']").classList.contains('active');
    });

    return {
      success: true,
      action: "Switched to Users tab",
      extractedData: { isUsersTabActive },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

