import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the trending hashtags to load
    await page.waitForSelector('.hashtag.trending');

    // Click on the first trending hashtag
    await page.click('.hashtag.trending:first-child');

    // Take a screenshot after clicking the hashtag
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_clicked_hashtag.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_clicked_hashtag.png'));

    return {
      success: true,
      action: "Clicked on the first trending hashtag",
      extractedData: {},
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the trending hashtags to be loaded
    await page.waitForSelector('.hashtag.trending');

    // Click on the first trending hashtag
    const firstTrendingHashtag = await page.$('.hashtag.trending:first-child');
    if (firstTrendingHashtag) {
      await firstTrendingHashtag.click();

      // Take a screenshot after clicking the hashtag
      await page.screenshot({ path: path.join(screenshotsDir, 'step_2_clicked_trending_hashtag.png') });
      screenshots.push(path.join(screenshotsDir, 'step_2_clicked_trending_hashtag.png'));

      return {
        success: true,
        action: "Clicked on the first trending hashtag",
        extractedData: {
          hashtag: await firstTrendingHashtag.innerText()
        },
        screenshots,
        error: null
      };
    } else {
      throw new Error("Trending hashtag element not found");
    }

  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the trending hashtags to load
    await page.waitForSelector('.hashtag.trending');

    // Click on the first trending hashtag
    const firstTrendingHashtag = await page.$('.hashtag.trending:first-child');
    if (firstTrendingHashtag) {
      await firstTrendingHashtag.click();
      await page.waitForLoadState('networkidle');
    }

    // Take a screenshot after clicking the hashtag
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_after_click.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_after_click.png'));

    return {
      success: true,
      action: "Clicked on the first trending hashtag",
      extractedData: {
        hashtag: await page.evaluate(el => el.innerText, firstTrendingHashtag)
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
