import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the first video container to load
    await page.waitForSelector('.video-container[data-video-id="1"]');

    // Click the bookmark button for the first video
    await page.click('.video-container[data-video-id="1"] .bookmark-btn');

    // Take a screenshot after clicking the bookmark button
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_bookmark_clicked.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_bookmark_clicked.png'));

    // Verify that the bookmark action was successful (this could be a check for a confirmation message or state change)
    const bookmarkStatus = await page.evaluate(() => {
      const button = document.querySelector('.video-container[data-video-id="1"] .bookmark-btn');
      return button ? button.classList.contains('bookmarked') : false;
    });

    return {
      success: true,
      action: "First video bookmarked successfully",
      extractedData: { bookmarkStatus },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

