import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the first video container to be available
    await page.waitForSelector('div.video-container[data-video-id="1"]');

    // Click the play/pause button for the first video
    await page.click('div.video-container[data-video-id="1"] .play-pause-btn');

    // Take a screenshot after clicking the button
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_play_pause_clicked.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_play_pause_clicked.png'));

    return {
      success: true,
      action: "Clicked the play/pause button for the first video",
      extractedData: {},
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

