import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the first video to be available
    await page.waitForSelector('.video-container', { timeout: 5000 });

    // Click the share button of the first video
    await page.click('.video-container .action-btn.share-btn');
    await page.waitForSelector('.share-modal', { timeout: 5000 });

    // Click the 'Copy Link' option in the share modal
    await page.click('text=Copy Link');
    await page.waitForTimeout(1000); // Wait for the action to complete

    // Take screenshot after clicking 'Copy Link'
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_copy_link_clicked.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_copy_link_clicked.png'));

    return {
      success: true,
      action: "Clicked 'Copy Link' in the share modal of the first video",
      extractedData: {},
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

