import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the video container with data-video-id='1' to be visible
    await page.waitForSelector('div.video-container[data-video-id="1"]');

    // Click the fullscreen button within the video container
    await page.click('div.video-container[data-video-id="1"] button.control-btn.fullscreen-btn');

    // Take a screenshot after clicking the fullscreen button
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_fullscreen_clicked.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_fullscreen_clicked.png'));

    return {
      success: true,
      action: "First video transitioned to fullscreen",
      extractedData: {},
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the video container to be visible
    await page.waitForSelector('div.video-container[data-video-id="1"]', { timeout: 5000 });

    // Click the fullscreen button within the video container
    const fullscreenButton = await page.$('div.video-container[data-video-id="1"] button.fullscreen-btn');
    if (fullscreenButton) {
      await fullscreenButton.click();
      await page.waitForTimeout(1000); // Wait for the fullscreen transition
    }

    // Take a screenshot after clicking the fullscreen button
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_fullscreen.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_fullscreen.png'));

    // Verify that the video is in fullscreen mode
    const isFullscreen = await page.evaluate(() => document.fullscreenElement !== null);

    return {
      success: true,
      action: "Fullscreen mode activated for the first video",
      extractedData: { isFullscreen },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

