export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the following users section to load
    await page.waitForSelector('.following-user');

    // Extract username and follower count from each user profile card
    const users = await page.$$eval('.following-user', userCards => {
      return userCards.map(card => {
        const username = card.querySelector('.username-selector').innerText; // Replace with actual selector
        const followers = card.querySelector('.followers-selector').innerText; // Replace with actual selector
        return { username, followers };
      });
    });

    // Log extracted data for verification
    console.log(users);

    return {
      success: true,
      action: "Extracted usernames and follower counts from all user profile cards",
      extractedData: users,
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the Following page to load and display user profiles
    await page.waitForSelector('.sidebar-item[data-page="following"]', { timeout: 5000 });
    await page.click('.sidebar-item[data-page="following"]');
    await page.waitForLoadState('networkidle');

    // Take screenshot of the Following page
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_following_page.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_following_page.png'));

    // Extract usernames and follower counts from .following-user elements
    const usersData = await page.evaluate(() => {
      const users = [];
      const userElements = document.querySelectorAll('.following-user');

      userElements.forEach(user => {
        const username = user.querySelector('.username-selector').textContent; // Adjust the selector as needed
        const followerCount = user.querySelector('.follower-count-selector').textContent; // Adjust the selector as needed
        users.push({ username, followerCount });
      });
      return users;
    });

    // Take screenshot after data extraction
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_data_extracted.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_data_extracted.png'));

    return {
      success: true,
      action: "Extracted usernames and follower counts from user profiles",
      extractedData: usersData,
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

