import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the first video container to be visible
    await page.waitForSelector('.video-container[data-video-id="1"]');

    // Click the bookmark button within the first video container
    await page.click('.video-container[data-video-id="1"] .bookmark-btn');

    // Take a screenshot after clicking the bookmark button
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_bookmark_clicked.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_bookmark_clicked.png'));

    return {
      success: true,
      action: "First video bookmarked successfully",
      extractedData: {},
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the video container with data-video-id='1' to be visible
    await page.waitForSelector('div.video-container[data-video-id="1"]');

    // Click the bookmark button within the video container
    await page.click('div.video-container[data-video-id="1"] .bookmark-btn');

    // Take a screenshot after clicking the bookmark button
