    screenshots.push(path.join(screenshotsDir, 'step_2_bookmark_clicked.png'));

    // Extract confirmation or relevant data to verify task completion
    const confirmationMessage = await page.evaluate(() => {
      return document.querySelector('.confirmation-message') ? document.querySelector('.confirmation-message').innerText : null;
    });

    return {
      success: true,
      action: "First video bookmarked successfully",
      extractedData: { confirmationMessage },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the first video container to be visible
    await page.waitForSelector('div.video-container[data-video-id="1"]', { timeout: 5000 });

    // Click the bookmark button within the first video container
    const bookmarkButtonSelector = 'div.video-container[data-video-id="1"] .bookmark-btn';
    await page.waitForSelector(bookmarkButtonSelector, { timeout: 5000 });
    await page.click(bookmarkButtonSelector);

    // Take a screenshot after clicking the bookmark button
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_bookmark_clicked.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_bookmark_clicked.png'));

    return {
      success: true,
      action: "Successfully added the first video to bookmarks",
      extractedData: {},
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
