import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the comment input and send button to be visible
    await page.waitForSelector('#commentInput');
    await page.waitForSelector('#commentSendBtn');

    // Input the comment
    await page.fill('#commentInput', 'Great content!');

    // Click the send button
    await page.click('#commentSendBtn');

    // Take a screenshot after sending the comment
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_comment_sent.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_comment_sent.png'));

    // Verify that the comment was sent (this could be a check for the comment appearing in the UI)
    const commentText = await page.locator('.comment-text').innerText();
    const commentSent = commentText.includes('Great content!');

    return {
      success: commentSent,
      action: "Comment 'Great content!' was sent successfully",
      extractedData: { comment: commentText },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

