import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the comment button to be visible and click it to open the comment modal
    await page.waitForSelector('button.action-btn.comment-btn', { timeout: 5000 });
    await page.click('button.action-btn.comment-btn');

    // Wait for the comment input field to be visible
    await page.waitForSelector('#commentInput', { timeout: 5000 });

    // Type 'Great content!' into the comment input
    await page.fill('#commentInput', 'Great content!');

    // Wait for the send button to be clickable and click it to submit the comment
    await page.waitForSelector('#commentSendBtn', { timeout: 5000 });
    await page.click('#commentSendBtn');

    // Take a screenshot after submitting the comment
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_comment_submitted.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_comment_submitted.png'));

    // Verify the comment was submitted (this may need adjustment based on the actual implementation)
    const commentText = await page.evaluate(() => {
      const commentElements = Array.from(document.querySelectorAll('.comment-text'));
      return commentElements.map(el => el.textContent).includes('Great content!');
    });

    return {
      success: true,
      action: "Comment 'Great content!' submitted successfully",
      extractedData: { commentSubmitted: commentText },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
