import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for video containers to load
    await page.waitForSelector('.video-container');

    // Extract like count, comment count, and video-id
    const videoData = await page.evaluate(() => {
      const videoContainers = document.querySelectorAll('.video-container');
      return Array.from(videoContainers).map(container => {
        const likeCount = container.querySelector('.like-btn span') ? container.querySelector('.like-btn span').innerText : '0';
        const commentCount = container.querySelector('.comment-btn span') ? container.querySelector('.comment-btn span').innerText : '0';
        const videoId = container.getAttribute('data-video-id');
        return {
          likeCount,
          commentCount,
          videoId
        };
      });
    });

    // Take a screenshot after extraction
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_extracted_data.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_extracted_data.png'));

    return {
      success: true,
      action: "Extracted like count, comment count, and video-id from all video containers",
      extractedData: videoData,
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

