import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the upload button to be visible and click it
    await page.waitForSelector('#uploadBtn', { state: 'visible' });
    await page.click('#uploadBtn');

    // Take a screenshot after clicking the upload button
    await page.waitForTimeout(1000); // Wait for the modal to open
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_upload_modal.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_upload_modal.png'));

    return {
      success: true,
      action: "Clicked the upload button to open the upload modal",
      extractedData: {},
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

