import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the volume slider to be available
    await page.waitForSelector('div.video-container[data-video-id="1"] input.volume-slider');

    // Set the volume slider to 50%
    const volumeSlider = await page.$('div.video-container[data-video-id="1"] input.volume-slider');
    await volumeSlider.evaluate(slider => {
      slider.value = 0.5; // Set the value to 50%
      slider.dispatchEvent(new Event('input', { bubbles: true })); // Trigger the input event
    });

    // Take a screenshot after setting the volume
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_volume_set.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_volume_set.png'));

    // Verify the volume is set correctly
    const currentVolume = await volumeSlider.evaluate(slider => slider.value);

    return {
      success: true,
      action: "Set the volume slider of the first video to 50%",
      extractedData: { currentVolume },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

