    // Wait for the volume slider in the video container with data-video-id='1'
    await page.waitForSelector('div.video-container[data-video-id="1"] input.volume-slider');

    // Drag the volume slider to set it to 50%
    const volumeSlider = await page.$('div.video-container[data-video-id="1"] input.volume-slider');
    const sliderBox = await volumeSlider.boundingBox();

    // Calculate the position to drag the slider to 50%
    const targetX = sliderBox.x + (sliderBox.width / 2);
    const targetY = sliderBox.y;

    // Perform the drag action
    await page.mouse.move(sliderBox.x + 5, targetY); // Move to the start of the slider
    await page.mouse.down();
    await page.mouse.move(targetX, targetY, { steps: 10 }); // Drag to 50%
