import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the first video container to load
    await page.waitForSelector('.video-container[data-video-id="1"]');

    // Click on the comment button of the first video
    const commentButton = await page.$('.video-container[data-video-id="1"] .action-btn.comment-btn');
    if (commentButton) {
      await commentButton.click();
      await page.waitForSelector('#commentInput'); // Wait for the comment modal to open
      await page.screenshot({ path: path.join(screenshotsDir, 'step_2_comment_modal_opened.png') });
      screenshots.push(path.join(screenshotsDir, 'step_2_comment_modal_opened.png'));
    }

    return {
      success: true,
      action: "Clicked the comment button of the first video and opened the comment modal",
      extractedData: {},
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
