import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the trending hashtags to load
    await page.waitForSelector('.hashtag.trending');

    // Click the first trending hashtag
    await page.click('.hashtag.trending:first-child');

    // Take a screenshot after clicking the hashtag
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_clicked_hashtag.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_clicked_hashtag.png'));

    return {
      success: true,
      action: "Clicked the first trending hashtag on the Discover page",
      extractedData: {},
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the trending hashtags to be visible
    await page.waitForSelector('.hashtag.trending', { timeout: 5000 });

    // Click the first trending hashtag
    const firstTrendingHashtag = await page.$('.hashtag.trending:first-of-type');
    if (firstTrendingHashtag) {
      await firstTrendingHashtag.click();
      await page.waitForLoadState('networkidle');

      // Take a screenshot after clicking the hashtag
      await page.screenshot({ path: path.join(screenshotsDir, 'step_2_clicked_hashtag.png') });
      screenshots.push(path.join(screenshotsDir, 'step_2_clicked_hashtag.png'));

      return {
        success: true,
        action: "Clicked the first trending hashtag",
        extractedData: {
          hashtag: await firstTrendingHashtag.innerText()
        },
        screenshots,
        error: null
      };
    } else {
      throw new Error('First trending hashtag not found');
    }
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

