import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the search input field to be visible
    await page.waitForSelector('#searchInput');

    // Type the hashtag into the search input
    await page.fill('#searchInput', '#dance');

    // Wait for the search button to be visible
    await page.waitForSelector('#searchBtn');

    // Click the search button
    await page.click('#searchBtn');

    // Take screenshot after clicking search
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_after_search.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_after_search.png'));

    // Verify the search results contain '#dance'
    const searchResultsExist = await page.evaluate(() => {
      return document.body.innerText.includes('#dance');
    });

    return {
      success: true,
      action: "Entered '#dance' into search input and clicked the search button",
      extractedData: { searchResultsExist },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
