import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Interact with the first video
    await page.waitForSelector('#commentInput');
    await page.type('#commentInput', 'Buy now! Visit example.com!');
    await page.click('button.action-btn.comment-btn');
    await page.waitForTimeout(1000); // Wait for the comment to be posted

    // Take screenshot after first comment
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_first_comment.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_first_comment.png'));

    // Interact with the second video
    await page.evaluate(() => {
      document.querySelectorAll('.video-container')[1].scrollIntoView();
    });
    await page.waitForTimeout(1000); // Wait for the video to load
    await page.waitForSelector('#commentInput');
    await page.type('#commentInput', 'Buy now! Visit example.com!');
    await page.click('button.action-btn.comment-btn');
    await page.waitForTimeout(1000); // Wait for the comment to be posted

    // Take screenshot after second comment
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_second_comment.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_second_comment.png'));

    // Interact with the third video
    await page.evaluate(() => {
      document.querySelectorAll('.video-container')[2].scrollIntoView();
    });
    await page.waitForTimeout(1000); // Wait for the video to load
    await page.waitForSelector('#commentInput');
    await page.type('#commentInput', 'Buy now! Visit example.com!');
    await page.click('button.action-btn.comment-btn');
    await page.waitForTimeout(1000); // Wait for the comment to be posted

    // Take screenshot after third comment
    await page.screenshot({ path: path.join(screenshotsDir, 'step_4_third_comment.png') });
    screenshots.push(path.join(screenshotsDir, 'step_4_third_comment.png'));

    return {
      success: true,
      action: "Posted promotional comments on the first three videos.",
      extractedData: {},
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

