import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the volume slider to be available
    await page.waitForSelector('div.video-container[data-video-id="1"] .volume-slider');

    // Set the volume slider to 50%
    const volumeSlider = await page.$('div.video-container[data-video-id="1"] .volume-slider');
    const sliderBoundingBox = await volumeSlider.boundingBox();
    const sliderWidth = sliderBoundingBox.width;

    // Calculate the position to drag the slider to 50%
    const targetPosition = sliderBoundingBox.x + (sliderWidth * 0.5);

    // Drag the slider to the target position
    await page.mouse.move(sliderBoundingBox.x + sliderWidth / 2, sliderBoundingBox.y + sliderBoundingBox.height / 2);
    await page.mouse.down();
    await page.mouse.move(targetPosition, sliderBoundingBox.y + sliderBoundingBox.height / 2, { steps: 10 });
    await page.mouse.up();

    // Take a screenshot after setting the volume
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_volume_set.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_volume_set.png'));

    return {
      success: true,
      action: "Set the volume slider of the first video to 50%",
      extractedData: {},
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the volume slider to be available in the DOM
    await page.waitForSelector('div.video-container[data-video-id="1"] input.volume-slider');

    // Set volume slider to 50%
    await page.evaluate(() => {
      const slider = document.querySelector('div.video-container[data-video-id="1"] input.volume-slider');
      slider.value = 0.5; // Set value to 50%
      slider.dispatchEvent(new Event('input', { bubbles: true })); // Trigger input event
    });

    // Take screenshot after the volume is set
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_volume_set.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_volume_set.png'));

    return {
      success: true,
      action: "Volume slider set to 50%",
      extractedData: {
        volume: 0.5
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the volume slider to be visible
    await page.waitForSelector('div.video-container[data-video-id="1"] input.volume-slider');

    // Set the volume slider to 50%
    const volumeSlider = await page.$('div.video-container[data-video-id="1"] input.volume-slider');
    await volumeSlider.evaluate(slider => {
      slider.value = 0.5; // Set to 50%
      slider.dispatchEvent(new Event('input', { bubbles: true })); // Trigger input event
    });

