    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_volume_set.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_volume_set.png'));

    // Verify the volume is set correctly
    const currentVolume = await volumeSlider.evaluate(slider => slider.value);

    return {
      success: true,
      action: "Set the volume slider of the first video to 50%",
      extractedData: { currentVolume },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
