import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the comment input field to be visible
    await page.waitForSelector('#commentInput');

    // Type the comment
    await page.fill('#commentInput', 'Great content!');

    // Wait for the send button to be visible and click it
    await page.waitForSelector('#commentSendBtn');
    await page.click('#commentSendBtn');

    // Take a screenshot after sending the comment
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_comment_sent.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_comment_sent.png'));

    return {
      success: true,
      action: "Comment 'Great content!' was successfully submitted.",
      extractedData: {},
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

