import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the following users section to load
    await page.waitForSelector('.following-user');

    // Extract usernames and follower counts
    const usersData = await page.evaluate(() => {
      const users = [];
      const userElements = document.querySelectorAll('.following-user');
      userElements.forEach(user => {
        const username = user.querySelector('.username-selector').innerText; // Replace with actual selector
        const followerCount = user.querySelector('.follower-count-selector').innerText; // Replace with actual selector
        users.push({ username, followerCount });
      });
      return users;
    });

    // Take a screenshot after extracting data
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_data_extracted.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_data_extracted.png'));

    return {
      success: true,
      action: "Extracted usernames and follower counts from all user profile cards",
      extractedData: usersData,
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

