import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the video container to load
    await page.waitForSelector('div.video-container[data-video-id="1"]');

    // Click the bookmark button in the first video container
    const bookmarkButton = await page.$('div.video-container[data-video-id="1"] .bookmark-btn');
    if (bookmarkButton) {
      await bookmarkButton.click();
      await page.waitForTimeout(1000); // Wait for any potential loading or animation
    }

    // Take screenshot after clicking the bookmark button
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_bookmark_clicked.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_bookmark_clicked.png'));

    // Verify the task completion by checking if the button has changed or any confirmation message appears
    const bookmarkStatus = await page.evaluate(() => {
      const button = document.querySelector('div.video-container[data-video-id="1"] .bookmark-btn');
      return button ? button.classList.contains('bookmarked') : false;
    });

    return {
      success: bookmarkStatus,
      action: "First video bookmarked successfully",
      extractedData: { bookmarked: bookmarkStatus },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
