import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the volume slider to be available
    await page.waitForSelector('div.video-container[data-video-id="1"] .volume-slider');

    // Set the volume slider to 50%
    const volumeSlider = await page.$('div.video-container[data-video-id="1"] .volume-slider');
    await volumeSlider.evaluate(slider => {
      slider.value = 0.5; // Set the value to 50%
      slider.dispatchEvent(new Event('input', { bubbles: true })); // Dispatch input event to update the volume
    });

    // Take screenshot after setting the volume
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_volume_set.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_volume_set.png'));

    // Verify the volume is set correctly
    const currentVolume = await volumeSlider.evaluate(slider => slider.value);

    return {
      success: true,
      action: "Set the volume slider to 50%",
      extractedData: { currentVolume },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the volume slider to be visible
    await page.waitForSelector('div.video-container[data-video-id="1"] input.volume-slider');

    // Drag the volume slider to set it to 50%
    const volumeSlider = await page.$('div.video-container[data-video-id="1"] input.volume-slider');
    const boundingBox = await volumeSlider.boundingBox();

    // Calculate the position for 50% volume
    const targetX = boundingBox.x + (boundingBox.width * 0.5); // 50% position
    const targetY = boundingBox.y + boundingBox.height / 2; // Center of the slider

    // Perform the drag action
    await page.mouse.move(boundingBox.x, targetY);
    await page.mouse.down();
    await page.mouse.move(targetX, targetY, { steps: 10 });
    await page.mouse.up();

    // Take a screenshot after adjusting the volume
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_volume_adjusted.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_volume_adjusted.png'));

    return {
      success: true,
      action: "Volume slider set to 50%",
      extractedData: {
        volume: 0.5 // Assuming we can read this after the action
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

