import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the like button to be visible and click it
    await page.waitForSelector("div.video-container[data-video-id='1'] .like-btn");
    await page.click("div.video-container[data-video-id='1'] .like-btn");

    // Take a screenshot after clicking the like button
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_like_clicked.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_like_clicked.png'));

    // Verify that the like button state has changed (e.g., check for a class change)
    const isLiked = await page.evaluate(() => {
      const likeButton = document.querySelector("div.video-container[data-video-id='1'] .like-btn");
      return likeButton.classList.contains('liked'); // Assuming 'liked' is the class for liked state
    });

    return {
      success: true,
      action: "Clicked the like button for the first video, changing its state to liked.",
      extractedData: { isLiked },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

