    // Extract the state of the like button to verify if liked successfully
    const isLiked = await page.$eval("div.video-container[data-video-id='1'] .like-btn", (btn) => btn.classList.contains('liked'));

    return {
      success: true,
      action: "Clicked the like button for the first video, changing its state to liked.",
      extractedData: { isLiked },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
