import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Navigate to profile page
    await page.waitForSelector('.sidebar-item[data-page="profile"]');
    await page.click('.sidebar-item[data-page="profile"]');
    await page.waitForSelector('.profile-header', { state: 'visible' });

    // Take screenshot of profile page
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_profile_page.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_profile_page.png'));

    // Extract profile information
    const profileData = await page.evaluate(() => {
      const username = document.querySelector('.profile-details h2')?.textContent || '';
      const followers = document.querySelectorAll('.stat strong')[1]?.textContent || '';
      const following = document.querySelectorAll('.stat strong')[0]?.textContent || '';
      const likes = document.querySelectorAll('.stat strong')[2]?.textContent || '';
      const videoCount = document.querySelectorAll('#junVideosGrid .video-thumbnail').length;

      return { username, followers, following, likes, videoCount };
    });

    // Take screenshot of profile videos
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_profile_videos.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_profile_videos.png'));

    // Click on first video to play it
    await page.waitForSelector('#junVideosGrid .video-thumbnail');
    await page.click('#junVideosGrid .video-thumbnail:first-child');

    // Wait for video to start playing in the feed
    await page.waitForSelector('.video-player[autoplay]', { state: 'visible', timeout: 5000 });

    // Take screenshot of playing video
    await page.screenshot({ path: path.join(screenshotsDir, 'step_4_video_playing.png') });
    screenshots.push(path.join(screenshotsDir, 'step_4_video_playing.png'));

    // Like the video
    await page.waitForSelector('.like-btn');
    await page.click('.like-btn');

    // Verify like was successful
    await page.waitForSelector('.like-btn.liked', { state: 'visible', timeout: 3000 });

    // Take screenshot after liking
    await page.screenshot({ path: path.join(screenshotsDir, 'step_5_video_liked.png') });
    screenshots.push(path.join(screenshotsDir, 'step_5_video_liked.png'));

    return {
      success: true,
      action: "Navigated to Jun's profile, extracted profile data, played first video, and liked it",
      extractedData: profileData,
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
