import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for and interact with search functionality
    await page.waitForSelector('#searchInput', { timeout: 5000 });
    await page.fill('#searchInput', '#dance');
    await page.click('#searchBtn');

    // Wait for search results and take screenshot
    await page.waitForTimeout(2000);
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_search.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_search.png'));

    // Interact with video controls - find first play button and click
    const playButtons = await page.$$('.play-pause-btn');
    if (playButtons.length > 0) {
      await playButtons[0].click();
      await page.waitForTimeout(1000);
    }

    // Like the first video
    const likeButtons = await page.$$('.like-btn');
    if (likeButtons.length > 0) {
      await likeButtons[0].click();
      await page.waitForTimeout(1000);
    }

    // Take screenshot after interactions
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_video_interaction.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_video_interaction.png'));

    // Try to add a comment
    await page.waitForSelector('#commentInput', { timeout: 3000 });
    await page.fill('#commentInput', 'Great video!');
    await page.click('#commentSendBtn');
    await page.waitForTimeout(1000);

    // Take final screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'step_4_comment_added.png') });
    screenshots.push(path.join(screenshotsDir, 'step_4_comment_added.png'));

    // Extract some data to verify actions
    const likeCount = await page.$eval('.like-btn span', el => el.textContent);
    const searchValue = await page.$eval('#searchInput', el => el.value);

    return {
      success: true,
      action: "Performed search, played video, liked content, and added comment",
      extractedData: {
        likeCount,
        searchQuery: searchValue,
        videosInteracted: likeButtons.length
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
