import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Search for content using the search input
    await page.waitForSelector('#searchInput', { timeout: 5000 });
    await page.fill('#searchInput', 'dance tutorial');
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_search_input.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_search_input.png'));

    // Click search button
    await page.click('#searchBtn');
    await page.waitForTimeout(1000);
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_search_clicked.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_search_clicked.png'));

    // Interact with video controls - click play/pause button on first video
    const playButtons = await page.$$('.play-pause-btn');
    if (playButtons.length > 0) {
      await playButtons[0].click();
      await page.waitForTimeout(1000);
      await page.screenshot({ path: path.join(screenshotsDir, 'step_4_video_play.png') });
      screenshots.push(path.join(screenshotsDir, 'step_4_video_play.png'));
    }

    // Like the first video
    const likeButtons = await page.$$('.like-btn');
    if (likeButtons.length > 0) {
      await likeButtons[0].click();
      await page.waitForTimeout(1000);
      await page.screenshot({ path: path.join(screenshotsDir, 'step_5_like_clicked.png') });
      screenshots.push(path.join(screenshotsDir, 'step_5_like_clicked.png'));
    }

    // Open comments on first video
    const commentButtons = await page.$$('.comment-btn');
    if (commentButtons.length > 0) {
      await commentButtons[0].click();
      await page.waitForTimeout(1000);
      await page.screenshot({ path: path.join(screenshotsDir, 'step_6_comments_opened.png') });
      screenshots.push(path.join(screenshotsDir, 'step_6_comments_opened.png'));
    }

    // Add a comment
    await page.waitForSelector('#commentInput', { timeout: 5000 });
    await page.fill('#commentInput', 'Great video!');
    await page.screenshot({ path: path.join(screenshotsDir, 'step_7_comment_entered.png') });
    screenshots.push(path.join(screenshotsDir, 'step_7_comment_entered.png'));

    // Send comment
    await page.click('#commentSendBtn');
    await page.waitForTimeout(1000);
    await page.screenshot({ path: path.join(screenshotsDir, 'step_8_comment_sent.png') });
    screenshots.push(path.join(screenshotsDir, 'step_8_comment_sent.png'));

    // Close comments modal
    await page.click('#closeCommentsModal');
    await page.waitForTimeout(1000);
    await page.screenshot({ path: path.join(screenshotsDir, 'step_9_comments_closed.png') });
    screenshots.push(path.join(screenshotsDir, 'step_9_comments_closed.png'));

    // Navigate to profile tab
    const profileTab = await page.$('button[data-page="profile"]');
    if (profileTab) {
      await profileTab.click();
      await page.waitForTimeout(1000);
      await page.screenshot({ path: path.join(screenshotsDir, 'step_10_profile_tab.png') });
      screenshots.push(path.join(screenshotsDir, 'step_10_profile_tab.png'));
    }

    return {
      success: true,
      action: "Performed search, video interaction, commenting, and profile navigation",
      extractedData: {
        searchPerformed: true,
        videoInteracted: true,
        commentAdded: true,
        profileAccessed: true
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete TikTok-like interactions",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
