import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Task 11: Interact with the third .volume-slider input and set its value to 0.7

    // Wait for all .volume-slider elements
    await page.waitForSelector('.volume-slider', { timeout: 5000 });
    const sliders = await page.$$('.volume-slider');
    if (sliders.length < 3) throw new Error('Less than 3 volume sliders found');

    // Set the third slider's value to 0.7
    await sliders[2].evaluate(slider => { slider.value = 0.7; slider.dispatchEvent(new Event('input', { bubbles: true })); });

    // Take screenshot after setting slider
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_slider_set.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_slider_set.png'));

    // Extract the value of all .volume-slider inputs for verification
    const sliderValues = await page.$$eval('.volume-slider', els => els.map(e => e.value));

    return {
      success: true,
      action: "Set the third volume slider to 0.7 and verified all slider values.",
      extractedData: {
        volumeSliderValues: sliderValues
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
