import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the messages list to be visible
    await page.waitForSelector('#messages-list', { timeout: 5000 });

    // Wait for at least one message to appear in the messages list
    // Try to select the first message (assuming messages are rendered as direct children)
    const firstMessageSelector = '#messages-list > div, #messages-list .message, #messages-list .message-item';
    await page.waitForSelector(firstMessageSelector, { timeout: 5000 });

    // Get the first message element handle
    const firstMessageHandle = await page.$(firstMessageSelector);
    if (!firstMessageHandle) throw new Error('First message not found');

    // Hover over the first message to reveal the reaction button (if needed)
    await firstMessageHandle.hover();
    await page.waitForTimeout(300); // Give time for any hover effects

    // Try to find the reaction button (commonly a smiley or emoji icon)
    // Try several likely selectors/texts
    let reactionButtonHandle = null;
    // Try by aria-label or title
    reactionButtonHandle = await firstMessageHandle.$('button[aria-label*="Add Reaction"], button[title*="Add Reaction"]');
    if (!reactionButtonHandle) {
      // Try by emoji icon (FontAwesome or similar)
      reactionButtonHandle = await firstMessageHandle.$('i.fa-smile, i.fa-face-smile, i.fas.fa-smile, i.fas.fa-face-smile');
    }
    if (!reactionButtonHandle) {
      // Try by button with emoji text
      reactionButtonHandle = await firstMessageHandle.$('button:has-text("üòÄ"), button:has-text("üòä"), button:has-text("üòÉ")');
    }
    if (!reactionButtonHandle) {
      // Try by generic button inside the message
      const buttons = await firstMessageHandle.$$('button');
      if (buttons.length === 1) reactionButtonHandle = buttons[0];
    }
    if (!reactionButtonHandle) {
      // Try to click at a likely position (simulate reaction hover)
      // If no reaction button is found, fail gracefully
      throw new Error('Reaction button not found on first message');
    }

    // Click the reaction button to open the emoji picker
    await reactionButtonHandle.click();
    await page.waitForTimeout(400);

    // Take screenshot after opening emoji picker
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_emoji_picker_opened.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_emoji_picker_opened.png'));

    // Wait for the emoji picker to appear
    // Try to find the thumbs up emoji in the picker
    // Try various selectors for thumbs up: text, aria-label, title, or unicode
    const thumbsUpSelectors = [
      'button[aria-label*="thumbs up"]',
      'button[title*="thumbs up"]',
      'button:has-text("üëç")',
      'span:has-text("üëç")',
      'div:has-text("üëç")',
      'button:has-text("Thumbs Up")',
      'button:has-text("thumbs up")'
    ];
    let thumbsUpHandle = null;
    for (const sel of thumbsUpSelectors) {
      thumbsUpHandle = await page.$(sel);
      if (thumbsUpHandle) break;
    }
    if (!thumbsUpHandle) {
      // Try to find any emoji button with unicode thumbs up
      thumbsUpHandle = await page.$('text=üëç');
    }
    if (!thumbsUpHandle) {
      // Try to find any emoji in the picker and pick the first one as fallback
      const emojiButtons = await page.$$('button, span');
      for (const btn of emojiButtons) {
        const text = await btn.evaluate(el => el.textContent || '');
        if (text.includes('üëç')) {
          thumbsUpHandle = btn;
          break;
        }
      }
    }
    if (!thumbsUpHandle) throw new Error('Thumbs up emoji not found in emoji picker');

    // Click the thumbs up emoji to add the reaction
    await thumbsUpHandle.click();
    await page.waitForTimeout(600);

    // Take screenshot after adding the reaction
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_thumbs_up_added.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_thumbs_up_added.png'));

    // Verify that the thumbs up reaction appears on the first message
    // Look for a thumbs up emoji or reaction count near the first message
    let reactionFound = false;
    const reactionSelectors = [
      'span:has-text("üëç")',
      'div:has-text("üëç")',
      'button:has-text("üëç")',
      '.reaction:has-text("üëç")'
    ];
    for (const sel of reactionSelectors) {
      const handle = await firstMessageHandle.$(sel);
      if (handle) {
        reactionFound = true;
        break;
      }
    }
    // As fallback, check if any thumbs up emoji is present in the first message
    if (!reactionFound) {
      const thumbsUpText = await firstMessageHandle.evaluate(el => el.innerText || '');
      if (thumbsUpText.includes('üëç')) reactionFound = true;
    }

    // Extracted data: whether thumbs up reaction is present
    const extractedData = {
      thumbsUpReactionPresent: reactionFound
    };

    return {
      success: reactionFound,
      action: "Added thumbs up reaction to the first message in the current channel",
      extractedData,
      screenshots,
      error: reactionFound ? null : "Thumbs up reaction not found after attempting to add"
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    const step1 = path.join(screenshotsDir, 'step_1_initial.png');
    await page.screenshot({ path: step1 });
    screenshots.push(step1);

    // Wait for the messages list to appear
    await page.waitForSelector('#messages-list', { timeout: 5000 });

    // Wait for at least one message to appear
    // Try to select the first message based on its structure (using nth-child and class heuristics)
    const firstMsgSelector = '#messages-list > div, #messages-list > .message, #messages-list > *:nth-child(1)';
    await page.waitForSelector(firstMsgSelector, { timeout: 5000 });

    // Find the first message element
    const firstMsgHandle = await page.$eval('#messages-list', (msgList) => {
      // Try common message class or just first child
      let firstMsg = msgList.querySelector('.message');
      if (!firstMsg) firstMsg = msgList.children[0];
      return firstMsg ? firstMsg.getAttribute('data-message-id') || firstMsg.id || null : null;
    });

    // Construct a robust selector for the first message
    let firstMessageElement;
    if (firstMsgHandle) {
      // Try to get by data-message-id or id
      firstMessageElement = await page.$(`[data-message-id="${firstMsgHandle}"], #${firstMsgHandle}`);
    } else {
      // Fallback: select first child
      const msgHandles = await page.$$('#messages-list > div, #messages-list > *');
      firstMessageElement = msgHandles[0];
    }

    // If the message element isn't found, throw
    if (!firstMessageElement) {
      throw new Error('Could not find first message element');
    }

    // Hover over the first message to reveal reactions/controls
    await firstMessageElement.hover();

    // Wait for the reaction add button to appear (search for emoji icon, '+', or similar)
    // Try several approaches: aria-label, title, innerText, or class names.
    // Try by visible button inside the message
    let reactionButton = await firstMessageElement.$('button[aria-label*="Add Reaction"], button[title*="Add Reaction"], button[aria-label*="reaction"], button, .add-reaction, [class*=reaction]');
    if (!reactionButton) {
      // Try to find a visible button with a "+" or emoji in it
      reactionButton = await firstMessageElement.$('button:visible, .add-reaction:visible');
      // As backup, search for any button inside the element
      if (!reactionButton) {
        const buttons = await firstMessageElement.$$('button');
        reactionButton = buttons.length ? buttons[0] : null;
      }
    }

    if (!reactionButton) {
      throw new Error('Could not find reaction add button on first message');
    }

    // Click the reaction button to open reaction picker
    await reactionButton.click();

    // Wait for the reaction picker to appear
    // Try to find any emoji picker or emoji button (by class, role, data-attribute)
    let emojiBtnSelector = 'button[aria-label*="thumb"], button[title*="thumb"], button, .emoji, [data-emoji*="thumb"], [class*=thumb]';
    await page.waitForSelector(emojiBtnSelector, { timeout: 3000 });

    // Find a reaction button with thumbs up
    // Try several strategies: aria-label, innerText, emoji unicode, etc.
    let thumbsUpBtn = await page.$(`${emojiBtnSelector}`);
    let found = false;
    if (thumbsUpBtn) {
      // Try to check if the button contains the üëç emoji or related label
      const btnLabel = await page.evaluate(el => el.getAttribute('aria-label') || el.getAttribute('title') || el.innerText || '', thumbsUpBtn);
      if (btnLabel.includes('üëç') || /thumb/i.test(btnLabel)) {
        // It looks like the correct button
        found = true;
      }
    }
    if (!found) {
      // Fallback: select any button that has 'üëç' in its innerText
      const emojiButtons = await page.$$(emojiBtnSelector);
      for (const btn of emojiButtons) {
        const text = await page.evaluate(el => el.innerText, btn);
        if (text.includes('üëç')) {
          thumbsUpBtn = btn;
          found = true;
          break;
        }
      }
    }

    if (!thumbsUpBtn) {
      throw new Error('Could not find thumbs up reaction button');
    }

    // Click the thumbs up emoji
    await thumbsUpBtn.click();

    // Wait for the reaction to appear on the first message (look for üëç or reaction count increment)
    // Try to find a reaction span or button in the message containing 'üëç'
    await page.waitForTimeout(500); // Wait for UI to update
    const reactionSelector = '.reaction, [class*=reaction], button, span';
    let thumbsUpVisible = false;
    const reactionElements = await firstMessageElement.$$(reactionSelector);
    for (const el of reactionElements) {
      const text = await page.evaluate(node => node.innerText || '', el);
      if (text.includes('üëç')) {
        thumbsUpVisible = true;
        break;
      }
    }

    // Take screenshot after reaction
    const step2 = path.join(screenshotsDir, 'step_2_thumbs_up.png');
    await page.screenshot({ path: step2 });
    screenshots.push(step2);

    // Extract the reactions of the first message for proof
    let extractedData = {};
    if (thumbsUpVisible) {
      extractedData = { thumbsUpReaction: true };
    } else {
      extractedData = { thumbsUpReaction: false };
    }

    return {
      success: thumbsUpVisible,
      action: "Added thumbs up reaction to the first message in the current channel",
      extractedData,
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    const step1 = path.join(screenshotsDir, 'step_1_initial.png');
    await page.screenshot({ path: step1 });
    screenshots.push(step1);

    // Wait for the messages list to load (using ID selector as shown in HTML)
    await page.waitForSelector('#messages-list', { timeout: 5000 });

    // Wait for at least one message to appear (use a generic descendant selector)
    // We'll look for a message by searching for the first .message element inside #messages-list
    // Since the HTML loads messages dynamically, we rely on rendered DOM
    const firstMessageSelector = '#messages-list .message';
    await page.waitForSelector(firstMessageSelector, { timeout: 5000 });

    // Locate the first message element
    const firstMessage = await page.$(firstMessageSelector);
    if (!firstMessage) {
      throw new Error('No message found in #messages-list');
    }

    // Hover over the first message to reveal the reaction (simulate user behavior)
    await firstMessage.hover();
    // Take a screenshot after hover (in case UI changes)
    const step2 = path.join(screenshotsDir, 'step_2_hovered_first_message.png');
    await page.screenshot({ path: step2 });
    screenshots.push(step2);

    // Try to find the "add reaction" button.
    // We'll look for a button or element with a recognizable emoji-plus or smiley icon.
    // Use various selector strategies: aria-label, title, visible text, data attributes, or icon class
    // Common in Discord clones: a button with an emoji or plus icon after hover

    // Strategy 1: Search for a button within the first message containing a smiley face, plus, or similar
    let addReactionButton = await firstMessage.$('button[aria-label*="reaction"], button[title*="reaction"], .fa-smile, .fa-plus, .fa-face-smile, .fa-grin');
    if (!addReactionButton) {
      // Fallback: search for a button with "+" or emoji text
      addReactionButton = await firstMessage.$('button:has-text("+"), button:has-text("üòä"), button:has-text("üòÄ")');
    }
    if (!addReactionButton) {
      // Fallback: try to find any button that appears after hovering (not present before)
      const buttons = await firstMessage.$$('button');
      if (buttons.length > 0) {
        addReactionButton = buttons[buttons.length - 1]; // Try the last button in message
      }
    }

    if (!addReactionButton) {
      throw new Error('Could not locate add reaction button in first message');
    }

    // Click the add reaction button
    await addReactionButton.click();

    // Wait for the reaction picker to appear (look for a popup, emoji list, or similar)
    // Try to find an emoji picker or emoji button
    // Look for a panel with emoji, or a button with a thumbs up emoji (üëç)
    await page.waitForTimeout(400); // Short wait for UI animation

    // Use a variety of selector strategies to find the thumbs up emoji
    // 1. Emoji with Unicode üëç
    // 2. Emoji button with title/text/aria-label containing "thumb"
    // 3. List item or button with "üëç" as text
    let thumbsUpSelector =
      'button:has-text("üëç"), [role=listbox] button:has-text("üëç"), [aria-label*="thumb"], [title*="thumb"], .emoji-picker .emoji, .emoji-list .emoji';
    let thumbsUpButton = await page.$(thumbsUpSelector);

    // If not found, try to get any visible "üëç" on the page (may not be button)
    if (!thumbsUpButton) {
      thumbsUpButton = await page.$('text=üëç');
    }

    // If still not found, try to find all emojis in the picker and select the first one
    if (!thumbsUpButton) {
      // Fallback: select the first emoji in the picker
      const possibleEmojis = await page.$$('button, span, div');
      for (const el of possibleEmojis) {
        const text = await el.textContent();
        if (text && text.includes('üëç')) {
          thumbsUpButton = el;
          break;
        }
      }
    }

    if (!thumbsUpButton) {
      throw new Error('Could not locate thumbs up emoji in emoji picker');
    }

    // Click the thumbs up emoji
    await thumbsUpButton.click();

    // Wait for reaction to appear on the first message
    // We'll check for a new element containing "üëç" as text inside the first message
    // Retry a few times for animation
    let reactionAppeared = false;
    for (let i = 0; i < 6; i++) {
      const reaction = await firstMessage.$(':text("üëç")');
      if (reaction) {
        reactionAppeared = true;
        break;
      }
      await page.waitForTimeout(300);
    }

    // Take screenshot after adding reaction
    const step3 = path.join(screenshotsDir, 'step_3_reaction_added.png');
    await page.screenshot({ path: step3 });
    screenshots.push(step3);

    // Extract confirmation data: check if the first message contains a thumbs up reaction
    let extractedData = {};
    if (reactionAppeared) {
      extractedData = { thumbsUpReaction: true };
    } else {
      extractedData = { thumbsUpReaction: false };
    }

    return {
      success: reactionAppeared,
      action: "Added thumbs up reaction to the first message in the current channel",
      extractedData,
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
