import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  const screenshots = [];
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Try to find and open the admin panel
    // Try common patterns: look for a gear/settings icon, or an "Admin" button/text
    let adminPanelOpened = false;
    let adminSelector = null;

    // Try by aria-label or title
    const possibleAdminSelectors = [
      // By icon (cog)
      'i.fa-cog',
      'i.fas.fa-cog',
      // By button or div containing "Admin"
      'text=Admin',
      'button:has-text("Admin")',
      'a:has-text("Admin")',
      // By settings
      'text=Settings',
      'button:has-text("Settings")',
      'a:has-text("Settings")',
      // By xpath for cog icon
      '//i[contains(@class,"fa-cog")]'
    ];

    for (const selector of possibleAdminSelectors) {
      try {
        const el = await page.waitForSelector(selector, { timeout: 1500 });
        if (el) {
          await el.click();
          adminPanelOpened = true;
          adminSelector = selector;
          break;
        }
      } catch (e) {
        // Not found, try next
      }
    }

    // Take screenshot after attempting to open admin/settings
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_admin_panel_attempt.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_admin_panel_attempt.png'));

    if (!adminPanelOpened) {
      return {
        success: false,
        action: "Failed to locate and open the admin/settings panel",
        extractedData: {},
        screenshots,
        error: "Admin/settings panel not found"
      };
    }

    // Wait for admin panel to appear (look for a button to generate test accounts)
    // Try to find a button with text "Generate Test Accounts" or similar
    let generateBtn = null;
    const possibleGenerateSelectors = [
      'button:has-text("Generate Test Accounts")',
      'text=Generate Test Accounts',
      'button:has-text("Generate Fake Users")',
      'text=Generate Fake Users',
      'button:has-text("Create Test Accounts")',
      'text=Create Test Accounts',
      '//button[contains(text(),"Generate Test Accounts")]',
      '//button[contains(text(),"Fake User")]'
    ];

    for (const selector of possibleGenerateSelectors) {
      try {
        generateBtn = await page.waitForSelector(selector, { timeout: 2000 });
        if (generateBtn) break;
      } catch (e) {
        // Try next
      }
    }

    // Take screenshot before clicking generate
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_found_generate_button.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_found_generate_button.png'));

    if (!generateBtn) {
      return {
        success: false,
        action: "Failed to locate 'Generate Test Accounts' button in admin panel",
        extractedData: {},
        screenshots,
        error: "Generate Test Accounts button not found"
      };
    }

    // Click the generate button
    await generateBtn.click();

    // Wait for accounts to be generated (look for a success message, or new users in a list)
    // Wait for either a toast, alert, or new user elements
    let generationSuccess = false;
    let generatedUsers = [];
    try {
      // Wait for a toast or alert
      await page.waitForSelector('text=Accounts generated', { timeout: 3000 });
      generationSuccess = true;
    } catch (e) {
      // Try to find new users in a user list (look for usernames like "testuser" or similar)
      try {
        // Wait for a user list to appear or update
        await page.waitForTimeout(1500);
        const userListItems = await page.$$('div.user-list .user-item, .user-list-item, .user-row, .username');
        for (const item of userListItems) {
          const text = await item.innerText();
          if (/test|fake|user/i.test(text)) {
            generatedUsers.push(text);
          }
        }
        if (generatedUsers.length > 0) generationSuccess = true;
      } catch (e2) {
        // No user list found
      }
    }

    // Take screenshot after generation
    await page.screenshot({ path: path.join(screenshotsDir, 'step_4_after_generation.png') });
    screenshots.push(path.join(screenshotsDir, 'step_4_after_generation.png'));

    if (!generationSuccess) {
      return {
        success: false,
        action: "Clicked 'Generate Test Accounts' but could not verify account creation",
        extractedData: {},
        screenshots,
        error: "No confirmation or new users detected"
      };
    }

    // Extract some data for verification
    let extractedData = {};
    if (generatedUsers.length > 0) {
      extractedData.generatedUsers = generatedUsers;
    } else {
      extractedData.message = "Accounts generated (confirmation message detected)";
    }

    return {
      success: true,
      action: "Accessed admin panel and generated fake user accounts",
      extractedData,
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    const step1 = path.join(screenshotsDir, 'step_1_initial.png');
    await page.screenshot({ path: step1 });
    screenshots.push(step1);

    // Try to access admin panel via server name dropdown or cog icon
    // First, click on the server name header to expand menu (try via text + icon)
    await page.waitForSelector('.server-header');
    await page.click('.server-header');
    await page.waitForTimeout(400); // slight delay for dropdown

    // Try to find a menu item with "Admin Panel" or similar
    // Use text selector just in case
    let adminPanelButton = null;
    try {
      adminPanelButton = await page.waitForSelector('text=/admin panel/i', { timeout: 1200 });
    } catch (e) {}
    if (!adminPanelButton) {
      // Try to find a cog/settings icon inside the server-header or in nearby DOM
      const cogIcon = await page.$('.server-header i.fa-cog, .server-header i.fa-gear');
      if (cogIcon) {
        await cogIcon.click();
      } else {
        // Try generic cog anywhere
        const cogAnywhere = await page.$('i.fa-cog, i.fa-gear');
        if (cogAnywhere) {
          await cogAnywhere.click();
        }
      }
      await page.waitForTimeout(600);
    } else {
      await adminPanelButton.click();
      await page.waitForTimeout(600);
    }

    // Take screenshot after opening admin (or attempting to)
    const step2 = path.join(screenshotsDir, 'step_2_admin_panel_attempt.png');
    await page.screenshot({ path: step2 });
    screenshots.push(step2);

    // Try to find "Generate Test Accounts" button by text or partial text
    let generateBtn = null;
    try {
      generateBtn = await page.waitForSelector('button:text("Generate Test Accounts")', { timeout: 1500 });
    } catch (e) {}
    if (!generateBtn) {
      // Try alternative selectors (partial text, custom attributes, or all buttons)
      const allButtons = await page.$$('button');
      for (let btn of allButtons) {
        const label = await btn.textContent();
        if (label && /generate.*test.*account/i.test(label)) {
          generateBtn = btn;
          break;
        }
      }
    }
    if (!generateBtn) {
      // Try xpath by partial text
      const [btnHandle] = await page.$x("//button[contains(translate(., 'ABCDEFGHIJKLMNOPQRSTUVWXYZ', 'abcdefghijklmnopqrstuvwxyz'), 'generate test account')]");
      if (btnHandle) generateBtn = btnHandle;
    }

    if (!generateBtn) {
      throw new Error('Could not find "Generate Test Accounts" button using any selector');
    }

    await generateBtn.click();
    await page.waitForTimeout(1200); // Wait for accounts to be generated

    // Take screenshot after generation
    const step3 = path.join(screenshotsDir, 'step_3_accounts_generated.png');
    await page.screenshot({ path: step3 });
    screenshots.push(step3);

    // Try to extract usernames/emails of generated accounts, e.g., from a table or list
    // Try for a table, list, or div with "test account" in text
    let fakeAccounts = [];
    // Try any element with class or id containing 'test' or 'account'
    const possibleLists = await page.$$('[class*=account], [class*=user], [id*=account], [id*=user]');
    for (let el of possibleLists) {
      const text = await el.innerText();
      if (/test.*account/i.test(text) || /fake.*user/i.test(text)) {
        fakeAccounts.push(text.trim());
      }
    }
    if (fakeAccounts.length === 0) {
      // fallback: all list items or rows, look for user-like text
      const listItems = await page.$$('li, tr');
      for (let el of listItems) {
        const text = await el.innerText();
        if (/test.*account/i.test(text) || /fake.*user/i.test(text) || /user/i.test(text)) {
          fakeAccounts.push(text.trim());
        }
      }
    }

    // Return result
    return {
      success: true,
      action: "Accessed admin panel and generated test user accounts via 'Generate Test Accounts' button",
      extractedData: {
        fakeAccounts
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    const step1 = path.join(screenshotsDir, 'step_1_initial.png');
    await page.screenshot({ path: step1 });
    screenshots.push(step1);

    // 1. Try to locate an 'admin panel' or equivalent
    // Since it's a Discord-like mock, let's search for settings/admin by icon
    // Try to click the 'cog' icon in '.user-controls' (could open user/admin settings)
    const cogIconSelector = '.user-controls i.fa-cog';
    await page.waitForSelector(cogIconSelector, { timeout: 5000 });
    await page.click(cogIconSelector);
    await page.waitForTimeout(700); // Wait for possible modal/panel to appear

    // Take screenshot after opening cog/settings
    const step2 = path.join(screenshotsDir, 'step_2_settings_opened.png');
    await page.screenshot({ path: step2 });
    screenshots.push(step2);

    // 2. Look for 'Generate Test Accounts' button or admin controls
    // Try to find button by text or possible class/id, using different selector strategies
    // First, try text selector (case-insensitive, whitespace trimmed)
    let foundButton = null;
    const buttonXPath = "//button[contains(translate(normalize-space(text()), 'ABCDEFGHIJKLMNOPQRSTUVWXYZ', 'abcdefghijklmnopqrstuvwxyz'), 'generate test accounts')]";
    const buttons = await page.$x(buttonXPath);
    if (buttons.length > 0) {
      foundButton = buttons[0];
    } else {
      // Fallback: search for button with 'test-accounts' in id/class
      const fallbackButton = await page.$("button[id*='test'], button[class*='test'], button[id*='account'], button[class*='account']");
      if (fallbackButton) foundButton = fallbackButton;
    }

    if (!foundButton) {
      throw new Error("Could not locate 'Generate Test Accounts' button or admin controls.");
    }

    // Click the 'Generate Test Accounts' button
    await foundButton.click();
    await page.waitForTimeout(1200); // Wait for account generation

    // Screenshot after generation
    const step3 = path.join(screenshotsDir, 'step_3_accounts_generated.png');
    await page.screenshot({ path: step3 });
    screenshots.push(step3);

    // 3. Extract newly created test account usernames (simulate: look for new user list, etc.)
    // Try to find elements that look like user accounts â€“ e.g. usernames in a member list
    let extractedUsernames = [];
    // Try common selectors for usernames after account generation
    const usernameSpans = await page.$$('div.member-list span.username, .user-list span.username, span.username');
    for (const span of usernameSpans) {
      const text = (await span.innerText()).trim();
      if (text && !extractedUsernames.includes(text)) extractedUsernames.push(text);
    }

    // If nothing found, fallback: try to get all visible usernames in the DOM (simulate extraction)
    if (extractedUsernames.length === 0) {
      const fallbackUsernames = await page.$$eval('*', nodes => {
        // Find all spans/divs with plausible username content
        return Array.from(nodes)
          .filter(n => n.textContent && n.textContent.match(/^[a-zA-Z0-9_]{3,}$/) && n.className && n.className.toLowerCase().includes('user'))
          .map(n => n.textContent.trim());
      });
      extractedUsernames = [...new Set(fallbackUsernames)];
    }

    return {
      success: true,
      action: "Accessed admin/settings panel and clicked 'Generate Test Accounts' to create fake user accounts.",
      extractedData: {
        fakeUsernames: extractedUsernames
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
