import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  const screenshots = [];
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');
    await page.waitForTimeout(500); // Allow any JS to initialize

    // Take initial screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Try to find an admin panel or menu
    // Look for settings/gear icon in user controls (bottom left)
    await page.waitForSelector('.user-controls .fa-cog', { timeout: 3000 });
    await page.click('.user-controls .fa-cog');
    await page.waitForTimeout(500);

    // Take screenshot after opening settings/admin panel
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_admin_panel.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_admin_panel.png'));

    // Look for a button or link to "Generate Test Accounts" in the settings/admin modal/panel
    // Try various selector strategies
    let foundGenerateButton = false;
    let buttonHandle = null;

    // Try by text (case-insensitive)
    const buttons = await page.$$('button, input[type="button"], a');
    for (const btn of buttons) {
      const text = (await btn.evaluate(el => el.innerText || el.value || '')).toLowerCase();
      if (text.includes('generate test account') || text.includes('generate fake user') || text.includes('generate accounts')) {
        buttonHandle = btn;
        foundGenerateButton = true;
        break;
      }
    }

    // If not found, try XPath for 'Generate Test Accounts'
    if (!foundGenerateButton) {
      const [xpathBtn] = await page.$x("//button[contains(translate(., 'ABCDEFGHIJKLMNOPQRSTUVWXYZ', 'abcdefghijklmnopqrstuvwxyz'), 'generate test account') or contains(translate(., 'ABCDEFGHIJKLMNOPQRSTUVWXYZ', 'abcdefghijklmnopqrstuvwxyz'), 'generate fake user') or contains(translate(., 'ABCDEFGHIJKLMNOPQRSTUVWXYZ', 'abcdefghijklmnopqrstuvwxyz'), 'generate accounts')]");
      if (xpathBtn) {
        buttonHandle = xpathBtn;
        foundGenerateButton = true;
      }
    }

    if (!foundGenerateButton) {
      throw new Error("Could not find 'Generate Test Accounts' button in admin/settings panel.");
    }

    // Click the Generate Test Accounts button
    await buttonHandle.click();
    await page.waitForTimeout(1000); // Wait for accounts to be generated

    // Take screenshot after generating accounts
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_accounts_generated.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_accounts_generated.png'));

    // Try to extract the list of new accounts (look for a user/member list)
    // Try to open member list if not already open
    const memberListToggle = await page.$('#toggle-member-list');
    if (memberListToggle) {
      await memberListToggle.click();
      await page.waitForTimeout(500);
    }

    // Try to extract usernames from member list (look for .member-list or similar)
    let userNames = [];
    const memberItems = await page.$$('.member-list .member-item, .member-list .user, .members-list .member, .user-list .user');
    if (memberItems.length > 0) {
      for (const item of memberItems) {
        const name = await item.evaluate(el => el.innerText || el.textContent || '');
        if (name && name.trim().length > 0) {
          userNames.push(name.trim());
        }
      }
    }

    // As fallback, try to extract usernames from any visible list after generation
    if (userNames.length === 0) {
      const possibleLists = await page.$$('ul, ol, div');
      for (const list of possibleLists) {
        const text = await list.evaluate(el => el.innerText || '');
        if (text.toLowerCase().includes('testuser') || text.toLowerCase().includes('fakeuser')) {
          userNames = text.split('\n').filter(line => line.match(/testuser|fakeuser/i));
          if (userNames.length > 0) break;
        }
      }
    }

    // Take screenshot of member list
    await page.screenshot({ path: path.join(screenshotsDir, 'step_4_member_list.png') });
    screenshots.push(path.join(screenshotsDir, 'step_4_member_list.png'));

    return {
      success: true,
      action: "Accessed admin panel and clicked 'Generate Test Accounts' to create multiple fake user accounts",
      extractedData: {
        generatedUsernames: userNames
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  const screenshots = [];
  try {
    // Step 1: Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Screenshot: Initial state
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Step 2: Attempt to access the admin panel
    // Try to find an element that could open an admin/settings panel.
    // Use cog/gear icon, often used for settings/admin.
    // Try various selector strategies - aria-label, title, role, icon class, XPath

    let adminPanelOpened = false;
    let settingsButton = null;

    // Try by icon class in .user-controls
    try {
      await page.waitForSelector('.user-controls .fa-cog', { timeout: 3000 });
      settingsButton = await page.$('.user-controls .fa-cog');
    } catch (e) {
      // fallback to search for any element with title or aria-label containing admin/settings
      const possibleSelectors = [
        '[title*=admin i]',
        '[title*=setting i]',
        '[aria-label*=admin i]',
        '[aria-label*=setting i]',
        'text=/admin/i',
        'text=/setting/i'
      ];
      for (const sel of possibleSelectors) {
        try {
          await page.waitForSelector(sel, { timeout: 1000 });
          settingsButton = await page.$(sel);
          if (settingsButton) break;
        } catch { /* ignore */ }
      }
    }

    if (settingsButton) {
      await settingsButton.click();
      adminPanelOpened = true;
      // Wait for possible admin panel to appear (simulate: look for new dialog or button)
      await page.waitForTimeout(1000);
      // Screenshot: After opening admin/settings
      await page.screenshot({ path: path.join(screenshotsDir, 'step_2_admin_panel.png') });
      screenshots.push(path.join(screenshotsDir, 'step_2_admin_panel.png'));
    }

    // Step 3: Look for 'Generate Test Accounts' button in the DOM
    // Use XPath and text-based locators for robustness
    let generateButton = null;
    let buttonSelectors = [
      "//button[contains(translate(., 'ABCDEFGHIJKLMNOPQRSTUVWXYZ', 'abcdefghijklmnopqrstuvwxyz'), 'generate test accounts')]",
      "//a[contains(translate(., 'ABCDEFGHIJKLMNOPQRSTUVWXYZ', 'abcdefghijklmnopqrstuvwxyz'), 'generate test accounts')]",
      "text=/generate test accounts/i",
      "button:has-text('Generate Test Accounts')",
      '[id*=generate]',
      '[class*=generate]'
    ];
    for (const sel of buttonSelectors) {
      try {
        if (sel.startsWith('//')) {
          // XPath
          generateButton = await page.$(`xpath=${sel}`);
        } else {
          // CSS or text
          await page.waitForSelector(sel, { timeout: 1000 });
          generateButton = await page.$(sel);
        }
        if (generateButton) break;
      } catch { /* ignore */ }
    }

    if (!generateButton) {
      // Try to find by visible text in all buttons (fallback)
      const allButtons = await page.$$('button');
      for (const btn of allButtons) {
        const btnText = (await btn.innerText()).toLowerCase();
        if (btnText.includes('generate test accounts')) {
          generateButton = btn;
          break;
        }
      }
    }

    if (!generateButton) {
      throw new Error("Could not find 'Generate Test Accounts' button in admin panel.");
    }

    // Click the Generate Test Accounts button
    await generateButton.click();
    // Wait for accounts to be generated (simulate: look for a success message or new users appearing)
    await page.waitForTimeout(2000);

    // Screenshot: After generating accounts
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_after_generate.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_after_generate.png'));

    // Step 4: Extract data to verify fake account creation
    // Try to find user list or a confirmation message
    let extractedData = {};
    let userList = [];
    // Look for elements that could be new user accounts
    // Try by class, aria-label, or text containing typical usernames
    const possibleUserListSelectors = [
      '.user-list',
      '.member-list',
      '.users',
      '[aria-label*=member]',
      '[aria-label*=user]'
    ];
    let usersFound = false;
    for (const sel of possibleUserListSelectors) {
      try {
        await page.waitForSelector(sel, { timeout: 1000 });
        const userEls = await page.$$(sel + ' .username, ' + sel + ' .user, ' + sel + ' li, ' + sel + ' span');
        if (userEls && userEls.length > 0) {
          for (const u of userEls) {
            const uname = (await u.innerText()).trim();
            if (uname && uname.length > 1) userList.push(uname);
          }
          usersFound = true;
          break;
        }
      } catch { /* ignore */ }
    }
    // Fallback: Try to find usernames anywhere in the DOM
    if (!usersFound) {
      const allSpans = await page.$$('span');
      for (const s of allSpans) {
        const txt = (await s.innerText()).trim();
        if (/^testuser\d+$/i.test(txt) || /^fakeuser\d+$/i.test(txt)) {
          userList.push(txt);
        }
      }
    }

    extractedData.generatedUsers = userList;
    extractedData.generatedUserCount = userList.length;

    // If no user list found, check for a success notification or message
    if (!userList.length) {
      const bodyText = await page.evaluate(() => document.body.innerText);
      const match = bodyText.match(/success.*generated|(\d+).*(fake|test).*accounts?/i);
      if (match) {
        extractedData.successMessage = match[0];
      }
    }

    return {
      success: true,
      action: "Accessed admin panel and clicked 'Generate Test Accounts' to create fake user accounts.",
      extractedData,
      screenshots,
      error: null
    };

  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  const screenshots = [];
  try {
    // Step 1: Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');
    await page.waitForTimeout(800); // Let JS initialize, as it's a SPA

    // Screenshot 1: Initial state
    const initialScreenshot = path.join(screenshotsDir, 'step_1_initial.png');
    await page.screenshot({ path: initialScreenshot });
    screenshots.push(initialScreenshot);

    // Step 2: Open the sidebar menu if needed (try both desktop and mobile)
    // Try the 'mobile-menu-toggle' if visible (mobile)
    const mobileToggleVisible = await page.$eval('#mobile-menu-toggle', el => window.getComputedStyle(el).display !== 'none');
    if (mobileToggleVisible) {
      await page.click('#mobile-menu-toggle');
      await page.waitForTimeout(500);
      const menuOpenShot = path.join(screenshotsDir, 'step_2_mobile_menu_open.png');
      await page.screenshot({ path: menuOpenShot });
      screenshots.push(menuOpenShot);
    }

    // Step 3: Try to find an admin panel or "Generate Test Accounts"
    // Try multiple selector strategies: text, partial text, xpath, etc.

    let foundAdminPanel = false;
    let foundGenerateButton = false;
    let adminPanelSelector = null;
    let generateButtonSelector = null;

    // 3a. Try to find a button or link with text "Admin" (case-insensitive)
    const adminCandidates = await page.$x("//button[contains(translate(., 'ADMIN', 'admin'), 'admin')] | //a[contains(translate(., 'ADMIN', 'admin'), 'admin')]");
    if (adminCandidates.length > 0) {
      await adminCandidates[0].click();
      foundAdminPanel = true;
      adminPanelSelector = 'xpath:button|a[text*=admin]';
      await page.waitForTimeout(600);
      const adminPanelShot = path.join(screenshotsDir, 'step_3_admin_panel.png');
      await page.screenshot({ path: adminPanelShot });
      screenshots.push(adminPanelShot);
    } else {
      // Try icon-based or sidebar-based admin access (cog icon, i.e. settings)
      const cogIcons = await page.$$('i.fa-cog, i.fas.fa-cog');
      if (cogIcons.length > 0) {
        await cogIcons[0].click();
        foundAdminPanel = true;
        adminPanelSelector = 'i.fa-cog';
        await page.waitForTimeout(600);
        const adminPanelShot = path.join(screenshotsDir, 'step_3_admin_panel_cog.png');
        await page.screenshot({ path: adminPanelShot });
        screenshots.push(adminPanelShot);
      }
    }

    // 3b. Try to find a button with text "Generate Test Accounts" (case-insensitive)
    let generateBtn = await page.$x("//button[contains(translate(., 'GENERATE TEST ACCOUNTS', 'generate test accounts'), 'generate test accounts')]");
    if (generateBtn.length === 0) {
      // Try partial: "Generate", "Test Account", etc.
      generateBtn = await page.$x("//button[contains(translate(., 'GENERATE', 'generate'), 'generate') or contains(translate(., 'ACCOUNT', 'account'), 'account')]");
    }
    if (generateBtn.length > 0) {
      await generateBtn[0].click();
      foundGenerateButton = true;
      generateButtonSelector = 'button[text*=Generate Test Accounts]';
      await page.waitForTimeout(900);

      const generateShot = path.join(screenshotsDir, 'step_4_generate_test_accounts.png');
      await page.screenshot({ path: generateShot });
      screenshots.push(generateShot);
    }

    // 3c. If no button found, try for any element that could be a "Generate Accounts" button (div, span)
    if (!foundGenerateButton) {
      const possibleGenerateBtn = await page.$x("//*[contains(translate(., 'GENERATE', 'generate'), 'generate') and (self::div or self::span)]");
      if (possibleGenerateBtn.length > 0) {
        await possibleGenerateBtn[0].click();
        foundGenerateButton = true;
        generateButtonSelector = 'div|span[text*=generate]';
        await page.waitForTimeout(900);

        const generateShot2 = path.join(screenshotsDir, 'step_4b_generate_test_accounts_alt.png');
        await page.screenshot({ path: generateShot2 });
        screenshots.push(generateShot2);
      }
    }

    // Step 4: Verification â€” Extract user list or a sign that test accounts were generated
    // Try to find user list in the sidebar or anywhere else (look for repeated user avatars or names)
    // Use multiple strategies for extraction

    // Find all usernames in .user-details .username
    let usernames = [];
    try {
      usernames = await page.$$eval('.user-details .username', nodes => nodes.map(n => n.textContent.trim()));
    } catch (e) {
      // ignore
    }

    // If not, try to find avatars with 'alt' that look like users
    let avatarAlts = [];
    try {
      avatarAlts = await page.$$eval('img[alt]', nodes => nodes.map(n => n.alt));
    } catch (e) {
      // ignore
    }

    // Combine extraction data
    const extractedData = {
      foundAdminPanel,
      adminPanelSelector,
      foundGenerateButton,
      generateButtonSelector,
      usernames,
      avatarAlts
    };

    // Reasoning: The action is successful if we navigated to admin panel and clicked a "Generate Test Accounts" button, or if we see more user accounts listed than before.
    return {
      success: foundGenerateButton || (usernames && usernames.length > 1),
      action: "Accessed admin panel and attempted to generate fake user accounts",
      extractedData,
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}
