import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  const screenshots = [];
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the messages list to be visible
    await page.waitForSelector('#messages-list', { timeout: 5000 });

    // Wait for at least one message to appear
    // Try to select the first message (assuming messages are rendered as children of #messages-list)
    const firstMessageSelector = '#messages-list > div, #messages-list .message, #messages-list .message-item';
    await page.waitForSelector(firstMessageSelector, { timeout: 5000 });

    // Find the first message element
    const firstMessageHandle = await page.$(firstMessageSelector);
    if (!firstMessageHandle) {
      throw new Error('No message found in the messages list');
    }

    // Hover over the first message to reveal reaction controls (if any)
    await firstMessageHandle.hover();
    await page.waitForTimeout(500); // Give time for UI to show reaction button

    // Try to find a reaction/add reaction button within the first message
    // Try common selectors: emoji, smiley, plus, or "Add Reaction" button
    // Try several possible selectors
    let reactionButtonHandle = null;
    const possibleReactionSelectors = [
      'button[aria-label*="Add Reaction"]',
      'button[title*="Add Reaction"]',
      '.add-reaction',
      '.reaction-add-button',
      '.fa-smile', // FontAwesome smiley
      '.fa-plus', // Sometimes a plus icon is used
      'button:has-text("üòÄ")',
      'button:has-text("üòä")',
      'button:has-text("üëç")'
    ];
    for (const sel of possibleReactionSelectors) {
      reactionButtonHandle = await firstMessageHandle.$(sel);
      if (reactionButtonHandle) break;
    }

    // If not found, try to find any button inside first message
    if (!reactionButtonHandle) {
      const buttons = await firstMessageHandle.$$('button');
      if (buttons.length > 0) {
        reactionButtonHandle = buttons[buttons.length - 1]; // Sometimes last button is reaction
      }
    }

    if (!reactionButtonHandle) {
      throw new Error('Could not find reaction/add reaction button in the first message');
    }

    // Click the reaction/add reaction button
    await reactionButtonHandle.click();
    await page.waitForTimeout(400);

    // Wait for reaction picker to appear
    // Try to find thumbs up emoji in the picker
    // Try selectors for thumbs up: text, emoji, or aria-label
    let thumbsUpSelector = [
      'button[aria-label*="thumbs up"]',
      'button[title*="thumbs up"]',
      'button:has-text("üëç")',
      '.emoji-picker .emoji[title*="thumbs up"]',
      '.emoji-picker .emoji:has-text("üëç")',
      '.emoji:has-text("üëç")'
    ];
    let thumbsUpButton = null;
    for (const sel of thumbsUpSelector) {
      thumbsUpButton = await page.$(sel);
      if (thumbsUpButton) break;
    }

    // If not found, try to find any emoji button with "üëç" text
    if (!thumbsUpButton) {
      thumbsUpButton = await page.$('button:has-text("üëç")');
    }

    if (!thumbsUpButton) {
      // Try to select any emoji with text content "üëç"
      const emojiHandles = await page.$$('button, span, div');
      for (const handle of emojiHandles) {
        const text = await handle.evaluate(el => el.textContent.trim());
        if (text === 'üëç') {
          thumbsUpButton = handle;
          break;
        }
      }
    }

    if (!thumbsUpButton) {
      throw new Error('Could not find thumbs up emoji in reaction picker');
    }

    // Click the thumbs up emoji
    await thumbsUpButton.click();
    await page.waitForTimeout(800);

    // Take screenshot after reaction is added
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_reaction_added.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_reaction_added.png'));

    // Extract data: check if thumbs up reaction now appears on the first message
    // Try to find a thumbs up emoji or reaction count under the first message
    let thumbsUpPresent = false;
    let thumbsUpCount = 0;

    // Look for a thumbs up emoji or reaction under the first message
    const reactionSelectors = [
      '.reaction', // generic
      '.reactions', // generic
      '.message-reactions',
      '.reaction-emoji',
      'span:has-text("üëç")',
      'button:has-text("üëç")'
    ];
    for (const sel of reactionSelectors) {
      const reactionHandle = await firstMessageHandle.$(sel);
      if (reactionHandle) {
        const text = await reactionHandle.evaluate(el => el.textContent);
        if (text.includes('üëç')) {
          thumbsUpPresent = true;
          // Try to extract count if present
          const match = text.match(/üëç\s*(\d*)/);
          if (match && match[1]) {
            thumbsUpCount = parseInt(match[1], 10) || 1;
          } else {
            thumbsUpCount = 1;
          }
          break;
        }
      }
    }

    // If not found, try to find any span/button with text "üëç"
    if (!thumbsUpPresent) {
      const possible = await firstMessageHandle.$$('span, button');
      for (const el of possible) {
        const text = await el.evaluate(e => e.textContent.trim());
        if (text.startsWith('üëç')) {
          thumbsUpPresent = true;
          const match = text.match(/üëç\s*(\d*)/);
          if (match && match[1]) {
            thumbsUpCount = parseInt(match[1], 10) || 1;
          } else {
            thumbsUpCount = 1;
          }
          break;
        }
      }
    }

    return {
      success: thumbsUpPresent,
      action: "Added thumbs up reaction to the first message in the current channel",
      extractedData: {
        thumbsUpPresent,
        thumbsUpCount
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}

