export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    const initialShot = path.join(screenshotsDir, 'step_1_initial.png');
    await page.screenshot({ path: initialShot });
    screenshots.push(initialShot);

    // Wait for the messages list to appear
    await page.waitForSelector('#messages-list', { timeout: 5000 });

    // Wait for at least one message to render
    // Use a generic selector for a message block, fallback to divs inside #messages-list
    // Try both .message and nth-child for robustness
    let firstMessageHandle = null;
    let firstMessageSelector = '';

    // Try several strategies to locate the first message
    const possibleSelectors = [
      '#messages-list > div', // direct child divs
      '#messages-list .message', // if class is present
      '//div[@id="messages-list"]/div[1]', // xpath
      '//div[@id="messages-list"]/*[1]' // xpath any element
    ];

    for (const sel of possibleSelectors) {
      try {
        if (sel.startsWith('//')) {
          const el = await page.$(sel);
          if (el) {
            firstMessageHandle = el;
            firstMessageSelector = sel;
            break;
          }
        } else {
          const el = await page.$(sel);
          if (el) {
            firstMessageHandle = el;
            firstMessageSelector = sel;
            break;
          }
        }
      } catch (e) {
        // skip to next selector
      }
    }

    if (!firstMessageHandle) {
      throw new Error('Could not locate the first message in the channel');
    }

    // Hover to reveal reaction controls if needed
    await firstMessageHandle.hover();
    await page.waitForTimeout(300); // slight wait for UI

    // Try to find the "Add Reaction" button within the first message
    // Try common selectors, aria-label, and text
    let addReactionBtn = null;
    const addReactionSelectors = [
      // aria-label or title
      '[aria-label="Add Reaction"]',
      '[title="Add Reaction"]',
      // Emoji icon button (FontAwesome smile or plus)
      'button:has(i.fa-smile)',
      'button:has(i.fa-plus)',
      // Button with emoji or text
      'button:visible:has-text("üòÄ")',
      // Fallback: any button inside first message
      'button'
    ];

    for (const sel of addReactionSelectors) {
      // Search within first message only
      try {
        const btn = await firstMessageHandle.$(sel);
        if (btn) {
          addReactionBtn = btn;
          break;
        }
      } catch (e) {}
    }

    if (!addReactionBtn) {
      // Try to find a reaction menu or icon directly
      // Fallback: try to click on rightmost button in the first message
      const btns = await firstMessageHandle.$$('button');
      if (btns.length > 0) addReactionBtn = btns[btns.length - 1];
    }

    if (!addReactionBtn) {
      throw new Error('Could not find Add Reaction button for the first message');
    }

    // Click the Add Reaction button
    await addReactionBtn.click();
    await page.waitForTimeout(300);

    // Take screenshot after opening reaction picker
    const pickerShot = path.join(screenshotsDir, 'step_2_reaction_picker.png');
    await page.screenshot({ path: pickerShot });
    screenshots.push(pickerShot);

    // Now select the "Thumbs Up" reaction (üëç)
    // Try common strategies: emoji text, aria-label, role, etc.
    // Try to find the emoji button in the popup/reaction picker
    let thumbsUpBtn = null;

    // Try to see if there's an emoji picker visible
    // Grab all emoji buttons and look for one that has 'üëç' in the text
    const emojiButtons = await page.$$('button, span, div');
    for (const btn of emojiButtons) {
      const text = await btn.evaluate(el => el.textContent || '');
      if (text.includes('üëç')) {
        thumbsUpBtn = btn;
        break;
      }
    }

    if (!thumbsUpBtn) {
      // Try to select by aria-label or title for thumbs up
      const thumbsSelectors = [
        '[aria-label="üëç"]',
        '[aria-label*="thumb"]',
        '[title="üëç"]',
        '[title*="thumb"]',
        'button:has-text("üëç")'
      ];
      for (const sel of thumbsSelectors) {
        try {
          const btn = await page.$(sel);
          if (btn) {
            thumbsUpBtn = btn;
            break;
          }
        } catch (e) {}
      }
    }

    if (!thumbsUpBtn) {
      throw new Error('Could not find Thumbs Up reaction emoji in picker');
    }

    // Click the thumbs up emoji to add as reaction
    await thumbsUpBtn.click();
    await page.waitForTimeout(500);

    // Screenshot after reaction added
    const afterReactShot = path.join(screenshotsDir, 'step_3_thumbs_up_added.png');
    await page.screenshot({ path: afterReactShot });
    screenshots.push(afterReactShot);

    // Extract: does the first message now have a thumbs up reaction displayed?
    // Look for a thumbs up emoji, icon, or reaction count in/near the first message
    let thumbsUpPresent = false;
    let thumbsUpCount = null;

    // Search all spans/divs/buttons inside first message for üëç
    const reactionEls = await firstMessageHandle.$$('span, div, button');
    for (const el of reactionEls) {
      const txt = await el.evaluate(e => e.textContent || '');
      if (txt.includes('üëç')) {
        thumbsUpPresent = true;
        // Try to also extract count (e.g., "üëç 1")
        const match = txt.match(/üëç\s*(\d*)/);
        if (match && match[1]) {
          thumbsUpCount = match[1];
        }
        break;
      }
    }

    return {
      success: thumbsUpPresent,
      action: "Added Thumbs Up reaction to the first message in the current channel",
      extractedData: {
        thumbsUpPresent,
        thumbsUpCount
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    const screenshots = [];
    // Take initial screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the messages list to be present
    await page.waitForSelector('#messages-list', { timeout: 5000 });

    // Wait for at least one message to appear (look for a message container)
    // Use a flexible selector: first child div of #messages-list
    const firstMessageSelector = '#messages-list > div, #messages-list .message, //div[@id="messages-list"]/div[1]';
    let firstMessageHandle = await page.$('#messages-list > div');
    if (!firstMessageHandle) {
      // Try alternative selectors if primary fails
      firstMessageHandle = await page.$('#messages-list .message');
    }
    if (!firstMessageHandle) {
      // Try XPath as a fallback
      const [msg] = await page.$x('//div[@id="messages-list"]/div[1]');
      firstMessageHandle = msg;
    }
    if (!firstMessageHandle) {
      throw new Error('First message not found in messages list');
    }

    // Hover over the first message to reveal the reaction/emoji controls
    await firstMessageHandle.hover();

    // Wait for the reaction/emoji add button to appear (try several strategies)
    // Possible icon: smiley face, plus, or emoji
    // Try to find a button with title or aria-label containing 'Add Reaction'
    let addReactionButton = await page.$('button[title*="Reaction"],button[aria-label*="Reaction"]');
    if (!addReactionButton) {
      // Try for a generic emoji button inside the first message (FontAwesome icon)
      addReactionButton = await firstMessageHandle.$('button i.fa-smile, button i.fas.fa-smile');
    }
    if (!addReactionButton) {
      // Try for a button with a plus and smiley or emoji
      addReactionButton = await firstMessageHandle.$('button i.fa-plus, button i.fas.fa-plus');
    }
    if (!addReactionButton) {
      // As a last resort, click the first visible button inside the message (if only one exists)
      const buttons = await firstMessageHandle.$$('button');
      if (buttons.length === 1) {
        addReactionButton = buttons[0];
      }
    }
    if (!addReactionButton) {
      throw new Error('Add Reaction button not found');
    }

    // Click the add reaction button
    await addReactionButton.click();

    // Wait for the emoji/reaction picker to appear
    // Look for a visible emoji panel, or a list of emojis; try several selectors
    let emojiButton;
    try {
      // Wait for the emoji picker to appear (allow some time)
      await page.waitForSelector('.emoji-picker, .emoji-list, [role="dialog"]', { timeout: 2000 });
      // Now select the thumbs up emoji
      // Try to find an emoji with title or alt containing 'thumb'
      emojiButton = await page.$('.emoji-picker [title*="thumb"], .emoji-picker [alt*="thumb"], .emoji-list [title*="thumb"], .emoji-list [alt*="thumb"], [role="dialog"] [title*="thumb"], [role="dialog"] [alt*="thumb"]');
      if (!emojiButton) {
        // Try XPath for Unicode thumbs up üëç
        const [thumbsUpNode] = await page.$x('//*[contains(text(), "üëç") or @alt="üëç" or @title="üëç"]');
        emojiButton = thumbsUpNode;
      }
      if (!emojiButton) {
        // Try any emoji option that may be first in the picker
        emojiButton = await page.$('.emoji-picker button, .emoji-list button, [role="dialog"] button');
      }
    } catch (e) {
      throw new Error('Emoji picker did not appear or no emoji found');
    }

    if (!emojiButton) {
      throw new Error('Thumbs Up emoji not found in picker');
    }

    // Click the thumbs up emoji to react
    await emojiButton.click();

    // Wait for the reaction to appear under the first message
    // Try to find a reaction bubble/button with thumbs up under the first message
    await page.waitForTimeout(800); // Allow UI update

    // Find the thumbs-up reaction under the first message
    let thumbsUpReaction = await firstMessageHandle.$('[aria-label*="thumb"], [title*="thumb"], .reaction, .reaction-btn, [class*=reaction]');
    let thumbsUpCount = null;
    if (thumbsUpReaction) {
      // Try to extract the emoji/text and count
      const reactionText = await thumbsUpReaction.innerText();
      thumbsUpCount = reactionText;
    }

    // Take screenshot after reaction
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_reaction_added.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_reaction_added.png'));

    return {
      success: true,
      action: "Added Thumbs Up reaction to the first message in the current channel",
      extractedData: {
        reactionConfirmed: !!thumbsUpReaction,
        reactionContent: thumbsUpCount
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
