    await thumbsUpHandle.click();
    await page.waitForTimeout(800);

    // Take screenshot after adding reaction
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_reaction_added.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_reaction_added.png'));

    // Extract data: check if thumbs up reaction is now visible on the first message
    // Look for a thumbs up emoji or reaction count in the first message
    let thumbsUpVisible = false;
    let reactionText = '';
